import React, { Component } from 'react';
import * as firebase from 'firebase'

var config = {
    apiKey: "AIzaSyDLC6QdeMl-SKZisjn6Py5up0spvkkwS14",
    authDomain: "onedoc-76a9b.firebaseapp.com",
    databaseURL: "https://onedoc-76a9b.firebaseio.com",
    projectId: "onedoc-76a9b",
    storageBucket: "",
    messagingSenderId: "518763913856"
  };
firebase.initializeApp(config);

class users extends Component {
    signIn = () => {
        const txtEmail = document.getElementById("txtEmail").value;
        const txtPassword = document.getElementById("txtPass").value;
        firebase.auth().onAuthStateChanged(firebaseUser => {
            if(firebaseUser){
                const user = firebase.auth().currentUser;
                if(user != null){
                    const user_id = user.email;
                    const user_verified = user.emailVerified;
                    window.log("hi");
                }

            }else{
                window.alert("No one is logged in");
            }
        });
        //Sign In
        firebase.auth().signInWithEmailAndPassword(txtEmail, txtPassword).catch((error) => {
            let errorCode = error.code;
            let errorMessage = error.message;
            window.alert("Error" + errorMessage);
        });

    }
	render() {
		return (
			<div className="users">
				<input type = "email" id = "txtEmail"></input> <br/>
                <input type = "password" id = "txtPass"></input>
                <button id = "btnSignIn" onClick = {this.signIn}>Sign in</button>
                <button id = "btnSignUp">Sign Up</button>
			</div>
		);
	}
}

export default users;
